mapper.select:
 select * from bjqcb bj join Unitsaccount un on bj.UnitInfoAccount=un.UnitInfoAccount join UnitInfo uf on un.UnitInfoId=uf.UnitInfoId 

 
    
         and uf.UnitInfoName like "%"#{UnitInfoName}"%"
    
    
         and bj.bjywllx like "%"#{bjywllx}"%"
    
    
        and bj.bjjzrq between #{date1} and #{date2}
    

  order by bj.bjbh desc
 limit #{startIndex},#{pageSize}
